import './App.css';
import Dashboard from './Pages/Dashboard/dashboard';
import Home from './Pages/Home/home';
import Sidebar from './Components/SideBar/sidebar';
import {Routes, Route, useNavigate} from 'react-router-dom';
import { useState, useEffect } from 'react';

function App() {
  const navigate = useNavigate(); // Đã sửa: thiếu dấu ()
  const [isLogin, setIsLogin] = useState(false);

  useEffect(() => {
    const isLogedIn = sessionStorage.getItem("isLogin");
    if(isLogedIn){
      setIsLogin(true);
      navigate("/dashboard");
    }
  }, [navigate]); // Đã sửa: thêm navigate vào mảng dependencies

  // Tạo một useEffect khác để theo dõi thay đổi trạng thái đăng nhập
  useEffect(() => {
    const handleStorageChange = () => {
      const isLogedIn = sessionStorage.getItem("isLogin");
      if(isLogedIn){
        setIsLogin(true);
        navigate("/dashboard");
      } else {
        setIsLogin(false);
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, [navigate]);

  return (
    <div className="flex">
      {
        isLogin && <Sidebar />
      }
      <Routes>
        <Route path='/' element={<Home/>} />
        <Route path='/dashboard' element={<Dashboard/>} />
      </Routes>
    </div>
  );
}

export default App;
