import React, { useState, useRef } from 'react';

const AddMembers = () => {
  const [image, setImage] = useState('https://cdn.pixabay.com/photo/2018/04/18/18/56/user-3331256_1280.png');
  const [isCameraOpen, setIsCameraOpen] = useState(false);
  const videoRef = useRef(null);
  const streamRef = useRef(null);

  // Kiểm tra xem thiết bị có hỗ trợ camera không
  const hasGetUserMedia = () => {
    return !!(navigator.mediaDevices && navigator.mediaDevices.getUserMedia);
  };

  // Mở camera
  const openCamera = async () => {
    if (!hasGetUserMedia()) {
      alert("Trình duyệt của bạn không hỗ trợ truy cập camera");
      return;
    }

    try {
      // Dừng stream hiện tại nếu có
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
      }

      // Tạo stream mới
      const stream = await navigator.mediaDevices.getUserMedia({
        video: {
          facingMode: 'user',
          width: { ideal: 640 },
          height: { ideal: 480 }
        }
      });

      // Lưu stream để dễ dàng dọn dẹp sau này
      streamRef.current = stream;

      // Hiển thị stream trong thẻ video
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        
        // Video đã sẵn sàng
        videoRef.current.onloadedmetadata = () => {
          videoRef.current.play();
          setIsCameraOpen(true);
          console.log("Camera đã được mở!");
        };
      }
    } catch (err) {
      console.error("Lỗi khi truy cập camera:", err);
      alert(`Không thể truy cập camera: ${err.message}`);
    }
  };

  // Chụp ảnh
  const takePicture = () => {
    // Nếu video đang chạy
    if (videoRef.current && streamRef.current) {
      // Lấy kích thước video thực tế
      const { videoWidth, videoHeight } = videoRef.current;
      
      // Tạo canvas với kích thước của video
      const canvas = document.createElement('canvas');
      canvas.width = videoWidth;
      canvas.height = videoHeight;
      
      // Vẽ frame hiện tại từ video lên canvas
      const ctx = canvas.getContext('2d');
      ctx.drawImage(videoRef.current, 0, 0, videoWidth, videoHeight);
      
      // Chuyển canvas thành data URL
      const imageDataURL = canvas.toDataURL('image/png');
      
      // Cập nhật state với ảnh mới
      setImage(imageDataURL);
      
      // Đóng camera
      closeCamera();
    }
  };

  // Đóng camera
  const closeCamera = () => {
    if (streamRef.current) {
      // Dừng tất cả các track của stream
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    
    setIsCameraOpen(false);
  };

  // Xử lý khi upload file
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setImage(event.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className='text-black'>
      <div className='grid gap-5 grid-cols-2 text-lg'>
        <input placeholder='Name' type='text' className='border-2 w-[90%] pl-3 pr-3 pt-2 pb-2 border-slate-400 rounded-md h-12'/>
        <input placeholder='Tel' type='text' className='border-2 w-[90%] pl-3 pr-3 pt-2 pb-2 border-slate-400 rounded-md h-12'/>
        <input placeholder='Address' type='text' className='border-2 w-[90%] pl-3 pr-3 pt-2 pb-2 border-slate-400 rounded-md h-12'/>
        <input type='date' className='border-2 w-[90%] pl-3 pr-3 pt-2 pb-2 border-slate-400 rounded-md h-12'/>

        <select className='border-2 w-[90%] h-12 pt-2 pb-2 border-slate-400 rounded-md placeholder:text-gray'>
          <option>1 Month Membership</option>
          <option>3 Month Membership</option>
        </select>

        <div className="flex flex-col gap-2">
          <input 
            type='file' 
            accept="image/*"
            onChange={handleFileChange} 
            className="w-[90%]"
          />
          
          <button 
            type="button"
            onClick={openCamera}
            className="border-2 p-2 rounded-md w-[90%] border-slate-400 flex items-center justify-center gap-2"
          >
            <span>📷</span> Mở camera
          </button>
        </div>

        <div className='w-1/4 relative'>
          {/* Hiển thị hình ảnh profile */}
          <img 
            src={image} 
            alt="Profile" 
            className='w-full h-full rounded-full object-cover border border-gray-300'
          />
        </div>

        <div className='p-3 border-2 w-28 text-lg h-14 text-center bg-slate-900 text-white rounded-xl cursor-pointer hover:bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500'>
          Register
        </div>
      </div>

      {/* Camera UI */}
      {isCameraOpen && (
        <div className="fixed top-0 left-0 right-0 bottom-0 bg-black bg-opacity-75 flex items-center justify-center z-50" 
             onClick={(e) => e.target === e.currentTarget && closeCamera()}>
          <div className="bg-white p-4 rounded-lg shadow-xl max-w-xl w-full">
            <h2 className="text-xl font-bold mb-4">Chụp ảnh</h2>
            
            {/* Video container */}
            <div className="relative bg-black rounded-md overflow-hidden" style={{minHeight: "360px"}}>
              <video
                ref={videoRef}
                className="w-full h-full object-cover"
                playsInline
                muted
                autoPlay
              />
            </div>
            
            {/* Camera controls */}
            <div className="flex justify-center mt-4 gap-4">
              <button
                onClick={takePicture}
                className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
              >
                Chụp ảnh
              </button>
              <button
                onClick={closeCamera}
                className="px-4 py-2 bg-gray-500 text-white rounded-md hover:bg-gray-600"
              >
                Hủy bỏ
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AddMembers;
