import React, { useState, useRef, useEffect } from 'react';

const AddMembers = () => {
  const [image, setImage] = useState('https://cdn.pixabay.com/photo/2018/04/18/18/56/user-3331256_1280.png');
  const [showCamera, setShowCamera] = useState(false);
  const videoRef = useRef(null);
  const streamRef = useRef(null);
  
  // Cleanup function to ensure camera is turned off when component unmounts
  useEffect(() => {
    // This will run when component unmounts
    return () => {
      if (streamRef.current) {
        streamRef.current.getTracks().forEach(track => track.stop());
        streamRef.current = null;
      }
    };
  }, []);

  const startCamera = async () => {
    try {
      console.log('Starting camera...');
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      streamRef.current = stream;
      
      if (videoRef.current) {
        console.log('Setting video source...');
        videoRef.current.srcObject = stream;
        
        // Add event listener to make sure video is loaded and playing
        videoRef.current.onloadedmetadata = () => {
          console.log('Video metadata loaded, playing video...');
          videoRef.current.play()
            .then(() => {
              console.log('Video is playing');
              setShowCamera(true);
            })
            .catch(e => console.error('Error playing video:', e));
        };
      }
    } catch (error) {
      console.error('Error accessing camera:', error);
      alert('Could not access camera. Please check permissions: ' + error.message);
    }
  };

  const stopCamera = () => {
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    
    setShowCamera(false);
  };

  const capturePhoto = () => {
    if (videoRef.current) {
      const canvas = document.createElement('canvas');
      canvas.width = videoRef.current.videoWidth;
      canvas.height = videoRef.current.videoHeight;
      
      const ctx = canvas.getContext('2d');
      ctx.drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);
      
      const photoUrl = canvas.toDataURL('image/png');
      setImage(photoUrl);
      stopCamera();
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setImage(event.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="text-black">
      <div className="grid gap-5 grid-cols-2 text-lg">
        <input 
          placeholder="Name" 
          type="text" 
          className="border-2 w-[90%] pl-3 pr-3 pt-2 pb-2 border-slate-400 rounded-md h-12"
        />
        <input 
          placeholder="Tel" 
          type="text" 
          className="border-2 w-[90%] pl-3 pr-3 pt-2 pb-2 border-slate-400 rounded-md h-12"
        />
        <input 
          placeholder="Address" 
          type="text" 
          className="border-2 w-[90%] pl-3 pr-3 pt-2 pb-2 border-slate-400 rounded-md h-12"
        />
        <input 
          type="date" 
          className="border-2 w-[90%] pl-3 pr-3 pt-2 pb-2 border-slate-400 rounded-md h-12"
        />

        <select className="border-2 w-[90%] h-12 pt-2 pb-2 border-slate-400 rounded-md placeholder:text-gray">
          <option>1 Month Membership</option>
          <option>3 Month Membership</option>
        </select>

        <div className="flex flex-col gap-2">
          <input 
            type="file" 
            accept="image/*" 
            onChange={handleFileChange} 
            className="w-[90%]"
          />
          <button 
            onClick={showCamera ? stopCamera : startCamera}
            className="flex items-center gap-2 border-2 p-2 rounded-md w-[90%] border-slate-400"
          >
            <span>ðŸ“·</span> {/* Using emoji instead of Lucide icon */}
            {showCamera ? 'Stop Camera' : 'Use Camera'}
          </button>
        </div>

        <div className="relative w-32 h-32">
          {!showCamera && (
            <img 
              src={image} 
              alt="Profile"
              className="w-full h-full rounded-full object-cover"
            />
          )}
          {showCamera && (
            <div className="absolute top-0 left-0 z-10 w-full h-full">
              <video 
                ref={videoRef} 
                autoPlay
                playsInline
                muted
                style={{width: '100%', height: '100%', borderRadius: '50%', objectFit: 'cover'}}
              />
              <button 
                onClick={capturePhoto}
                className="absolute bottom-0 left-1/2 transform -translate-x-1/2 bg-red-500 text-white p-1 rounded-full"
              >
                Capture
              </button>
            </div>
          )}
        </div>

        <div className="p-3 border-2 w-28 text-lg h-14 text-center bg-slate-900 text-white rounded-xl cursor-pointer hover:bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500">
          Register
        </div>
      </div>
    </div>
  );
};

export default AddMembers;
