import React, { useState, useRef, useEffect } from 'react';

const AddMembers = () => {
  const [image, setImage] = useState('https://cdn.pixabay.com/photo/2018/04/18/18/56/user-3331256_1280.png');
  const [cameraActive, setCameraActive] = useState(false);
  const videoRef = useRef(null);
  const streamRef = useRef(null);

  // Cleanup camera when component unmounts
  useEffect(() => {
    return () => {
      stopCamera();
    };
  }, []);

  // Start the camera
  const startCamera = async () => {
    try {
      // Request camera access
      const stream = await navigator.mediaDevices.getUserMedia({ 
        video: true 
      });
      
      // Store the stream reference
      streamRef.current = stream;
      
      // Connect stream to video element
      if (videoRef.current) {
        videoRef.current.srcObject = stream;
        setCameraActive(true);
      }
    } catch (error) {
      console.error('Camera access error:', error);
      alert('Kh√¥ng th·ªÉ truy c·∫≠p camera: ' + error.message);
    }
  };

  // Stop the camera
  const stopCamera = () => {
    // Stop all tracks
    if (streamRef.current) {
      streamRef.current.getTracks().forEach(track => track.stop());
      streamRef.current = null;
    }
    
    // Update state
    setCameraActive(false);
  };

  // Take a photo
  const capturePhoto = () => {
    if (videoRef.current) {
      // Create canvas with video dimensions
      const canvas = document.createElement('canvas');
      const videoWidth = videoRef.current.videoWidth;
      const videoHeight = videoRef.current.videoHeight;
      
      canvas.width = videoWidth;
      canvas.height = videoHeight;
      
      // Draw current video frame to canvas
      const context = canvas.getContext('2d');
      context.drawImage(videoRef.current, 0, 0, videoWidth, videoHeight);
      
      // Convert canvas to image data URL
      const photoUrl = canvas.toDataURL('image/png');
      
      // Update profile image
      setImage(photoUrl);
      
      // Close camera
      stopCamera();
    }
  };

  // Handle file upload
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        setImage(event.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className='text-black'>
      {/* Camera modal - only shows when camera is active */}
      {cameraActive && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white p-4 rounded-lg max-w-lg w-full">
            <h3 className="text-lg font-bold mb-2">Camera</h3>
            
            {/* Video element for camera feed */}
            <div className="relative">
              <video 
                ref={videoRef} 
                autoPlay 
                playsInline
                className="w-full border border-gray-300 rounded"
              />
              
              {/* Camera controls */}
              <div className="flex justify-between mt-4">
                <button 
                  onClick={capturePhoto}
                  className="bg-blue-500 text-white px-4 py-2 rounded"
                >
                  Ch·ª•p ·∫£nh
                </button>
                <button 
                  onClick={stopCamera}
                  className="bg-red-500 text-white px-4 py-2 rounded"
                >
                  H·ªßy b·ªè
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Main form */}
      <div className='grid gap-5 grid-cols-2 text-lg'>
        <input placeholder='Name' type='text' className='border-2 w-[90%] pl-3 pr-3 pt-2 pb-2 border-slate-400 rounded-md h-12'/>
        <input placeholder='Tel' type='text' className='border-2 w-[90%] pl-3 pr-3 pt-2 pb-2 border-slate-400 rounded-md h-12'/>
        <input placeholder='Address' type='text' className='border-2 w-[90%] pl-3 pr-3 pt-2 pb-2 border-slate-400 rounded-md h-12'/>
        <input type='date' className='border-2 w-[90%] pl-3 pr-3 pt-2 pb-2 border-slate-400 rounded-md h-12'/>

        <select className='border-2 w-[90%] h-12 pt-2 pb-2 border-slate-400 rounded-md placeholder:text-gray'>
          <option>1 Month Membership</option>
          <option>3 Month Membership</option>
        </select>

        {/* Image upload and camera options */}
        <div className="flex flex-col gap-2">
          <input 
            type='file' 
            accept="image/*"
            onChange={handleFileChange} 
            className="w-[90%]"
          />
          
          <button 
            type="button"
            onClick={startCamera}
            className="border-2 p-2 rounded-md w-[90%] border-slate-400 flex items-center justify-center gap-2"
          >
            <span>üì∑</span> M·ªü camera
          </button>
        </div>

        {/* Profile image display */}
        <div className='w-1/4'>
          <img 
            src={image} 
            alt="Profile" 
            className='w-full h-full rounded-full object-cover border border-gray-300'
          />
        </div>

        {/* Submit button */}
        <div className='p-3 border-2 w-28 text-lg h-14 text-center bg-slate-900 text-white rounded-xl cursor-pointer hover:bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500'>
          Register
        </div>
      </div>
    </div>
  );
};

export default AddMembers;
